"""
    parse_konect(content::String)

Helper function to parse network data from the [KONECT Project](http://konect.cc) and return it to a graph object in the JuliaGraphs ecosystem.

# Examples
```julia
# read the data from a file
G = open(io -> parse_konect(read(io, String)), "/path/to/KONECT/network/data")
# store the graph in Graphs.jl format
savegraph("path/to/my_KONECT_graph.lgz", G)
```
"""
function parse_konect(content::String)
    # split content into lines
    lines = split(content, "\n")
    ## Determine type of graph
    types = split(lines[1], " ")
    @info types
    # graph
    if types[2] == "sym" && types[3] == "posweighted"
        Gt = Graphs.SimpleWeightedGraph
    elseif types[2] == "asym" && types[3] == "posweighted"
        Gt = Graphs.SimpleWeightedDiGraph
    elseif types[2] == "sym" && types[3] == "unweighted"
        Gt = Graphs.SimpleGraph
    elseif types[2] == "asym" && types[3] == "unweighted"
        Gt = Graphs.SimpleDiGraph
    elseif types[2] == "bip" && types[3] == "unweighted"
        Gt = Graphs.SimpleGraph
    else
        # temporary solution, add bipartite too later
        throw(ArgumentError("The graph type is not supported."))
    end
    ## Determine properties of graph
    properties = split(lines[2], " ")

    NE = parse(Int, properties[2])
    if types[2] ∈ ["sym"; "asym"]    
        NV = parse(Int, properties[3])
        ## create graph
        G = Gt(NV)
        # add edges
        for line in lines[3:end-1]
            # split line into components
            components = split(line, " ")
            if components[3] == ""
                # unweighted
                Graphs.add_edge!(G, parse(Int, components[1]), parse(Int, components[2]))
            else
                # weighted
                @info components
                Graphs.add_edge!(G, parse(Int, components[1]), parse(Int, components[2]), parse(Float64, components[3]))
            end
        end

        ## quality check
        @assert NE == Graphs.ne(G)
        @assert NV == Graphs.nv(G)

    elseif types[2] == "bip"
        N⊥, N⊤ = parse(Int, properties[3]), parse(Int, properties[4])
        NV = N⊥ + N⊤
        ## create graph
        G = Gt(NV)
        # add edges
        for line in lines[3:end-1]
            # split line into components
            components = split(line, " ")
            if components[3] == ""
                # unweighted
                Graphs.add_edge!(G, parse(Int, components[1]), parse(Int, components[2])+ N⊥)
            else
                # weighted
                @info components
                Graphs.add_edge!(G, parse(Int, components[1]), parse(Int, components[2]), parse(Float64, components[3]+ N⊥))
            end
        end

        ## quality check
        @assert NE == Graphs.ne(G)
        @assert NV == Graphs.nv(G)
        membership = Graphs.bipartite_map(G)
        @assert sum(membership .== UInt8(1)) == N⊥
        @assert sum(membership .== UInt8(2)) == N⊤
    end

    
    
    return G
end

"""
    readpajek(f::String; is_directed::Bool=true)

helper function to load a network from the [pajek dataset](http://vlado.fmf.uni-lj.si/pub/networks/data/bio/foodweb/foodweb.htm) and return it to a graph object in the JuliaGraphs ecosystem.

For this simple helper, weights are discarded.
"""
function readpajek(f::String; is_directed::Bool=true)
    G = is_directed ? Graphs.SimpleDiGraph() : Graphs.SimpleGraph()
    nv = r"\*vertices\s(\d+)"
    arcs = r"\*arcs"
    arc = r"(\d+)\s+(\d+)"
    arcing = false
    for line in readlines(f)
        if !isnothing(match(nv, line))
            N = parse(Int,(match(nv, line).captures[1]))
            Graphs.nv(G) == 0 && Graphs.add_vertices!(G, N) 
        end

        if !isnothing(match(arcs, line))
            arcing = true
            continue
        end

        if arcing 
            # check empty line
            if isempty(line)
                arcing = false
                return G
            end

            src, dst = parse.(Int, match(arc, line).captures)
            Graphs.add_edge!(G, src, dst)
        end
    end
end



